let images=document.querySelectorAll(".thumbnail"),mainslide=document.querySelector(".mainslide");function init(){mainslide.src=images[0].src}function updateImage(e){let i=e.target;mainslide.src=i.src,mainslide.classList.add("animate__fadeIn"),setTimeout(()=>{mainslide.classList.remove("animate__fadeIn")},800)}images.forEach(e=>{e.addEventListener("click",updateImage)}),init();